        private static readonly (int dx, int dy)[] Directions = [(0, -1), (1, 0), (0, +1), (-1, 0)];

        private enum MapFeature {Wall = -1, End = -2, Start = -3}

        private static long[,]? _map;

        private static (long part1, long part2) MoveInMap((int x, int y) position, HashSet<(int x, int y, int dirIdx)>? visited = null, int dirIdx = 1, long points = 0)
        {

            (long part1, long part2) retval; 

            visited ??= [];

            switch (_map![position.x, position.y])
            {
                case (int) MapFeature.Wall:
                    retval = (long.MaxValue, 0);
                    break;
                case (int)MapFeature.End:
                    visited.Add((position.x, position.y, dirIdx));
                    retval = (points, 0);
                    break;
                default:
                {
                    if (_map[position.x, position.y] != (int)MapFeature.Start && _map[position.x, position.y] < points && _map[position.x, position.y] + 1000 != points)
                    {
                        retval = (long.MaxValue, 0);
                    }
                    else
                    {
                        HashSet<(int x, int y, int dirIdx)> visitedAhead = [];
                        HashSet<(int x, int y, int dirIdx)> visitedLeft = [];
                        HashSet<(int x, int y, int dirIdx)> visitedRight = [];

                        _map[position.x, position.y] = points;

                        visited.Add((position.x, position.y, dirIdx));

                        int dirLeftIdx = (dirIdx + 4 - 1) % 4;
                        int dirRightIdx = (dirIdx + 1) % 4;

                        long pointsAhead = MoveInMap((position.x + Directions[dirIdx].dx, position.y + Directions[dirIdx].dy), visitedAhead, dirIdx, points + 1).part1;
                        long pointsLeft = MoveInMap((position.x + Directions[dirLeftIdx].dx, position.y + Directions[dirLeftIdx].dy), visitedLeft, dirLeftIdx, points + 1001).part1;
                        long pointsRight = MoveInMap((position.x + Directions[dirRightIdx].dx, position.y + Directions[dirRightIdx].dy), visitedRight, dirRightIdx, points + 1001).part1;

                        long minPoints = Math.Min(pointsAhead, Math.Min(pointsRight, pointsLeft));

                        if (minPoints != long.MaxValue)
                        {
                            if (minPoints == pointsAhead)
                            {
                                visited.UnionWith(visitedAhead);
                            }

                            if (minPoints == pointsLeft)
                            {
                                visited.UnionWith(visitedLeft);
                            }

                            if (minPoints == pointsRight)
                            {
                                visited.UnionWith(visitedRight);
                            }
                        }

                        retval = (minPoints, visited.DistinctBy(v => (v.x, v.y)).Count());
                    }

                    break;
                }
            }

            return retval;
        }

        private static void Execute(string[] inputdata)
        {
            _map = inputdata.SelectMany((line, row) => line.Select((ch, col) => (ch, col, row))).Aggregate(new long[inputdata[0].Length, inputdata.Length], (curr, next) =>
            {
                curr[next.col, next.row] = next.ch switch {'#' => (int)MapFeature.Wall, 'E' => (int)MapFeature.End, 'S' => (int)MapFeature.Start, _ => int.MaxValue};
                return curr;
            });

            (int col, int row) startPos = Enumerable.Range(0, _map.Length).Select(i => (col: i % _map.GetLength(1), row: i / _map.GetLength(1))).First(pos => _map[pos.col, pos.row] == (int)MapFeature.Start);

            (long part1, long part2) result = MoveInMap(startPos);


            Console.WriteLine($"Day 16 Part 1 Answer is calibration value {result.part1} points.");
            Console.WriteLine($"Day 16 Part 2 Answer is calibration value {result.part2} tiles.");
        }

        public static void Run() => Execute(Data);
    }
